import React, { useState } from 'react';
import { Box, Button, TextField, Typography, Grid } from '@mui/material';

const TomasuloSimulator = () => {
  const [instructions, setInstructions] = useState('');
  const [currentCycle, setCurrentCycle] = useState(0);
  const [reservationStations, setReservationStations] = useState({
    add: Array(3).fill(null),
    mult: Array(2).fill(null),
    load: Array(2).fill(null),
  });

  const handleStep = () => {
    setCurrentCycle(prev => prev + 1);
    // TODO: 实现Tomasulo算法的单步执行逻辑
  };

  const handleReset = () => {
    setCurrentCycle(0);
    setReservationStations({
      add: Array(3).fill(null),
      mult: Array(2).fill(null),
      load: Array(2).fill(null),
    });
  };

  return (
    <Box>
      <Grid container spacing={3}>
        <Grid item xs={12}>
          <TextField
            fullWidth
            multiline
            rows={4}
            label="输入指令"
            value={instructions}
            onChange={(e) => setInstructions(e.target.value)}
            placeholder="请输入MIPS指令，每行一条"
          />
        </Grid>
        <Grid item xs={12}>
          <Box sx={{ display: 'flex', gap: 2 }}>
            <Button variant="contained" onClick={handleStep}>
              单步执行
            </Button>
            <Button variant="outlined" onClick={handleReset}>
              重置
            </Button>
          </Box>
        </Grid>
        <Grid item xs={12}>
          <Typography variant="h6">当前周期: {currentCycle}</Typography>
        </Grid>
        <Grid item xs={12}>
          <Typography variant="h6">保留站状态:</Typography>
          <Box sx={{ mt: 2 }}>
            <Typography>加法保留站:</Typography>
            {reservationStations.add.map((station, index) => (
              <Typography key={index}>
                站 {index + 1}: {station ? JSON.stringify(station) : '空闲'}
              </Typography>
            ))}
          </Box>
        </Grid>
      </Grid>
    </Box>
  );
};

export default TomasuloSimulator;
