import React, { useState, useRef, useCallback, useEffect } from 'react';
import { Box, Button, TextField, Typography, Grid, Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow } from '@mui/material';
import { parseInstructions } from '../utils/instructionParser';
import { TomasuloCore } from '../utils/tomasuloCore';

const TomasuloSimulator = () => {
  const [instructions, setInstructions] = useState('');
  const [error, setError] = useState('');
  const [updateTrigger, setUpdateTrigger] = useState(0);
  const simulatorRef = useRef(new TomasuloCore());

  // 初始化模拟器
  useEffect(() => {
    // 初始化内存值
    simulatorRef.current.initializeMemory([
      { address: 0, value: 5 },  // 示例值
      { address: 1, value: 10 },
      { address: 2, value: 15 }
    ]);
  }, []);

  const handleStep = useCallback(() => {
    try {
      if (simulatorRef.current.instructionQueue.length === 0 && instructions.trim()) {
        const parsedInstructions = parseInstructions(instructions);
        simulatorRef.current.loadInstructions(parsedInstructions);
      }
      simulatorRef.current.step();
      setUpdateTrigger(prev => prev + 1);
      setError('');
    } catch (err) {
      setError(err.message);
    }
  }, [instructions]);

  const handleReset = useCallback(() => {
    simulatorRef.current.reset();
    // 重新初始化内存值
    simulatorRef.current.initializeMemory([
      { address: 0, value: 5 },
      { address: 1, value: 10 },
      { address: 2, value: 15 }
    ]);
    setUpdateTrigger(prev => prev + 1);
    setError('');
  }, []);

  const renderReservationStations = useCallback(() => {
    const stations = simulatorRef.current.reservationStations;
    return (
      <TableContainer component={Paper}>
        <Table size="small">
          <TableHead>
            <TableRow>
              <TableCell>FU</TableCell>
              <TableCell>Busy</TableCell>
              <TableCell>OP</TableCell>
              <TableCell>Vj</TableCell>
              <TableCell>Vk</TableCell>
              <TableCell>Qj</TableCell>
              <TableCell>Qk</TableCell>
              <TableCell>Dest</TableCell>
              <TableCell>Remaining Cycles</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {Object.entries(stations).map(([type, stationList]) => (
              stationList.map((station, index) => (
                <TableRow key={`${type}-${index}`}>
                  <TableCell>{station.name}</TableCell>
                  <TableCell>{station.busy ? '是' : '否'}</TableCell>
                  <TableCell>{station.op || '-'}</TableCell>
                  <TableCell>{station.vj !== null ? station.vj : '-'}</TableCell>
                  <TableCell>{station.vk !== null ? station.vk : '-'}</TableCell>
                  <TableCell>{station.qj || '-'}</TableCell>
                  <TableCell>{station.qk || '-'}</TableCell>
                  <TableCell>{station.dest || '-'}</TableCell>
                  <TableCell>{station.remainingCycles || '-'}</TableCell>
                </TableRow>
              ))
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    );
  }, [updateTrigger]);

  const renderRegisters = useCallback(() => {
    const registers = simulatorRef.current.registers;
    return (
      <TableContainer component={Paper}>
        <Table size="small">
          <TableHead>
            <TableRow>
              <TableCell>寄存器</TableCell>
              <TableCell>值</TableCell>
              <TableCell>Qi</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {registers.map((reg, index) => (
              <TableRow key={index}>
                <TableCell>R{index}</TableCell>
                <TableCell>{reg.value}</TableCell>
                <TableCell>{reg.qi || '-'}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    );
  }, [updateTrigger]);

  return (
    <Box>
      <Grid container spacing={3}>
        <Grid item xs={12}>
          <TextField
            fullWidth
            multiline
            rows={4}
            label="输入指令"
            value={instructions}
            onChange={(e) => setInstructions(e.target.value)}
            placeholder="请输入MIPS指令，每行一条&#10;例如：&#10;LD R1, 0&#10;ADD R2, R1, R1&#10;MUL R3, R2, R2"
            error={!!error}
            helperText={error}
          />
        </Grid>
        <Grid item xs={12}>
          <Box sx={{ display: 'flex', gap: 2 }}>
            <Button variant="contained" onClick={handleStep}>
              单步执行
            </Button>
            <Button variant="outlined" onClick={handleReset}>
              重置
            </Button>
          </Box>
        </Grid>
        <Grid item xs={12}>
          <Typography variant="h6">当前周期: {simulatorRef.current.currentCycle}</Typography>
        </Grid>
        <Grid item xs={12}>
          <Typography variant="h6" gutterBottom>保留站状态:</Typography>
          {renderReservationStations()}
        </Grid>
        <Grid item xs={12}>
          <Typography variant="h6" gutterBottom>寄存器状态:</Typography>
          {renderRegisters()}
        </Grid>
      </Grid>
    </Box>
  );
};

export default TomasuloSimulator;
